mapper.select:
SELECT admin_id, password, admin_name, profile_img, gender, email, admin_desc, create_time, last_edit_time
FROM admin
WHERE admin_id = #{adminId}

mapper.update:
UPDATE admin
password=#{password}, admin_name=#{adminName}, profile_img=#{profileImg}, gender=#{gender}, email=#{email}, admin_desc=#{adminDesc}, last_edit_time=#{lastEditTime},
WHERE admin_id = #{adminId}
