mapper.select:
SELECT teacher_id, password, teacher_name, profile_img, gender, email, teacher_desc, create_time, last_edit_time
FROM teacher
WHERE teacher_id = #{teacherId}

mapper.update:
UPDATE teacher
password=#{password}, teacher_name=#{teacherName}, profile_img=#{profileImg}, gender=#{gender}, email=#{email}, teacher_desc=#{teacherDesc}, last_edit_time=#{lastEditTime},
WHERE teacher_id = #{teacherId}
